            onDismiss={()=>history.push('/')} />
    );
    }
}

const mapStateToProps=(state,ownProps)=>{
    return {stream:state.streams[ownProps.match.params.id]}
}

export default connect(mapStateToProps,{fetchStream,deleteStream}) (StreamDelete);
